import React, { useState } from "react";
import ProjectCard from "./ProjectCard";

function Projects() {
  const [projects, setProjects] = useState();

  const handleSetProject = (e) => {
    setProjects(e.target.value);
  };

  const projectData = [
    {
      pStatus: { selectedStatus: 1 },
      pName: "RG",
    },
    {
      pStatus: { selectedStatus: 2 },
      pName: "RG2",
    },
    {
      pStatus: { selectedStatus: 1 },
      pName: "RG1",
    },
  ];
  const filteredProject = projectData.filter((e) => {
    if (projects == 0) {
      return e.pStatus.selectedStatus;
    } else {
      return e.pStatus.selectedStatus == projects;
    }
  });

  return (
    <>
      <div className="container">
        <div className="sectionProject d-flex justify-content-start align-items-center gap-3">
          <label htmlFor="project" className="fs-5 fw-bold">
            Select Project's :
          </label>
          <select
            className="px-3 py-2 fw-bold rounded-2 bg-secondary outline-danger"
            name="project"
            id=""
            onClick={handleSetProject}
          >
            <option className="p-2 fw-bold" value="0">All</option>
            <option className="p-2 fw-bold" value="1">Frontend</option>
            <option className="p-2 fw-bold" value="2">Backend</option>
          </select>
        </div>

        <div className="d-flex justify-content-start gap-5 align-content-center py-3 py-md-5">
          {filteredProject.map((item, index) => (
            <ProjectCard data={item} />
          ))}
        </div>
      </div>
    </>
  );
}

export default Projects;
