import React, { useState } from "react";

function Projects() {
  const [projects, setProjects] = useState();

  const handleSetProject = (e) => {
    setProjects(e.target.value);
  };

  const projectData = [
    {
      pStatus: { selectedStatus: 1 },
      pName: "RG",
    },
    {
      pStatus: { selectedStatus: 2 },
      pName: "RG2",
    },
    {
      pStatus: { selectedStatus: 1 },
      pName: "RG1",
    },
  ];
  const filteredProject = projectData.filter((e) => {
    if (projects == 0) {
      return e.pStatus.selectedStatus;
    } else {
      return e.pStatus.selectedStatus == projects;
    }
  });

  return (
    <>
      <div className="container">
        <div className="d-flex ">
          <label htmlFor="project" >Select Project's</label>
          <select className="p-2 rounded-2 bg-secondary outline-danger" name="project" id="" onClick={handleSetProject}>
            <option value="0">All</option>
            <option value="1">Frontend</option>
            <option value="2">Backend</option>
          </select>
        </div>

        {filteredProject.map((e) => {
          return (
            <div className="container text-light">
              <p>{e.pName}</p>
            </div>
          );
        })}
      </div>
    </>
  );
}

export default Projects;
